function (head, ctx) {
 var back = {"id": [], "key": [], "count": 0};
// var dbg = [];
	var row ;
	var rpp = ctx.query.rpp ? ctx.query.rpp : 20;
	var i = 1;
	while (row = getRow() ) {
 	//dbg.push ( {"i": i, "doc": row.id} );
		if ( i == 1 ) {
		  back.id.push ( row.id  ) ;
		  back.key.push ( row.key ) ;
		} else if ( i == rpp ) {
		  i=0;
		}
		i++;back.count++;
	}
	return JSON.stringify({"pages":back});
}

